import { useState } from 'react'; //共享状态的useState 方法 rect hooks

export default function useStats(url) {
  // console.log(url);
  // from input 双向绑定新写法 
  const [stats, setStats] = useState(); 
  // 加载ajax loading
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState();
  return {
    stats,
    loading, 
    error
  }
}
